import React, { Component } from 'react';


class Create extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imagedata: String,
            name: ''

        };
        this.addFormData = this.addFormData.bind(this);
    }

    addFormData(evt) {
        evt.preventDefault();
        const fd = new FormData();

        fd.append('image', this.state.imagedata);
        fd.append('name', this.state.name);

       
        //Post Request to laravel API Route
        axios.post('add-product', fd
        ).then(res => {
            document.getElementById('createFrom').reset();
            //Success Message in Sweetalert modal
            Swal.fire({
                title: 'Image has been uploaded successfully.',
                text: "Thanks",
                type: 'success',

            });

        }
        );
    }

    render() {
        return (
            <>
                <div class="container">
                    <div class="row">
                        <div class="col-3"></div>
                        <div class="col-6">
                            <br/><br/>
                            <h1>App Product</h1>
                            <br/>
                            <form onSubmit={this.addFormData} id='createFrom'>
                                <div class="form-group">
                                    
                                    <input type="text" onChange={(e) => { this.setState({ title: e.target.value }) }} class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Title" />
                                    
                                </div>
                                <div class="form-group">
                                    
                                    <input type="text" onChange={(e) => { this.setState({ price: e.target.value }) }} class="form-control" id="exampleInputPassword1" placeholder="Price" />
                                </div>
                                <div class="form-group">
                                    
                                    <input type="file" onChange={(e) => { this.setState({ image: e.target.files[0] }) }} class="form-control" id="exampleInputPassword1"  />
                                </div>
                                <div class="form-group">
                                    
                                    <input type="text" onChange={(e) => { this.setState({ description: e.target.value }) }} class="form-control" id="exampleInputPassword1" placeholder="Description"  />
                                </div>
                                <button type="submit" class="btn btn-primary">Submit</button>
                            </form>
                        </div>
                        <div class="col-3"></div>
                    </div>
                </div>
            </>
        );
    }
}

export default Create;
