import React, { Component } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
const baseURL = 'http://localhost/admin/';
class Main extends Component {

    constructor(props) {
        super(props);
           /* Initialize the state. */
           this.state = {
            products: []
            }
         
        //Boilerplate code for binding methods with `this`
        this.deleteData = this.deleteData.bind(this);
        // this.handleInput = this.handleInput.bind(this);
      }

      componentDidMount() {
        axios.get('/view-product')
            .then((response) => {
                // handle success
                //console.log(response);
                this.setState({ products: response.data })
            })
            .catch((error) => {
                // handle error
                console.log(error);
            });
    }

    deleteData = (id) => {

        // axios.get('/delete-product/' + id)
        // .then((response) => {
        //     // handle success
        //     console.log(response);

        //     // let products = this.state.products;
        //     // for (var i = 0; i < products.length; i++) {
        //     //     if (products[i].id == id) {
        //     //         products.splice(i, 1);
        //     //         this.setState({ products: products })
        //     //     }

        //     // }
        // })


        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            // icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            alert('yes')
            if (result.isConfirmed) {
               alert('yes')
                // Swal.fire(
                //     'Deleted!',
                //     'Your file has been deleted.',
                //     'success'
                // )
            }
        })
    }
    
    render(){

        const products = this.state.products
        const allProducts = products.map((product, key) => {
            return (
                <>
                    <div class="col-4">
                            <div class="card" style={{margin:"5px"}}>
                                
                                <img class="card-img-top" width='50px' src={baseURL + product.image} alt='not found' />
                                <div class="card-body">
                                    <h5 class="card-title">{product.title}</h5>
                                    <p class="card-text">{product.description}</p>
                                    <a href="#" class="btn btn-primary">TK. {product.price}</a>&nbsp;
                                    <Link><button class="btn btn-success">Edit</button></Link>&nbsp;
                                    <button onClick={this.deleteData.bind(this, key)} class="btn btn-danger">Remove</button>
                                </div>
                            </div>
                        </div>
                </>
            )
        })

        return (
            <>
                <div class="container">
                    <div class="row">
                       
                    {allProducts}

                    </div>    
    
                </div>
            </>
        );
    }
    
}

export default Main;
